import { EventEmitter, Output } from '@angular/core';
import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';
import { IMascotas } from '@modules/mascotas/interface/mascotas.interface';
import { MascotasservService } from '@modules/mascotas/services/mascotasserv.service';
import { ToastrService } from 'ngx-toastr';


@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})
export class CardComponent {
  @Input() obj!:IMascotas;

  @Output()
  eliminar=new EventEmitter<IMascotas>();


  constructor(private mascotasService: MascotasservService, private toastr: ToastrService, private router: Router){}
    borrar(pet:IMascotas){
  }

  eliminarPets(obj:IMascotas){
    this.eliminar.emit(obj);
  }
}
<!--<p>card works!</p>-->
<div class="d-flex p-2 m-2"></div>
<div class="card" style="width: 15rem;">
  <img [src]="obj.img" class="card-img-top col-img" alt="...">
  <div class="card-body">
    <h5 class="card-title">{{ obj.raza }}</h5>
    <p>
      <strong>{{ obj.origen }}</strong>
    </p>
    <p class="card-text"><strong>Peso </strong>{{ obj.peso }}</p>
    <p class="card-text"><strong>Guardian </strong>{{ obj.guardian }}</p>
    <a [routerLink]="['/mascotas/', obj.id]" class="btn btn-primary">Leer más</a>
    <a (click)="eliminarPets(obj)" class="btn btn-warning m-1">Quitar</a>
  </div>
</div>
