import { Component, OnInit } from '@angular/core';
import { IMascotas } from '@modules/mascotas/interface/mascotas.interface';
import { ActivatedRoute, Router } from '@angular/router';
import { switchMap } from 'rxjs';
import { MascotasservService } from '@modules/mascotas/services/mascotasserv.service';

@Component({
  selector: 'app-mascota',
  templateUrl: './mascota.component.html',
  styleUrls: ['./mascota.component.scss']
})
export class MascotaComponent implements OnInit{

  mascota!: IMascotas;
  constructor(
    private activatedRoute: ActivatedRoute,
    private mascotasService: MascotasservService,
    private router: Router
    ) {}

    ngOnInit(): void {
      //como devuelve un observable
      //witchMap, operador de transformacion
      this.activatedRoute.params
      .pipe(switchMap(({ id }) => this.mascotasService.mascotasById(id)))
      .subscribe((resp: any) => {
        this.mascota = resp;
      });
    }
    regresar() {
      // para ir a la ruta indicada
      this.router.navigate(['mascotas/listar']);
    }

    eliminar(){
      this.activatedRoute.params
      .pipe(switchMap(({ id }) => this.mascotasService.mascotasDelete(id)))
      .subscribe((resp: any) => {
        this.mascota = resp;

      });
      this.regresar();
    }



}
<!--<p>mascota works!</p>-->
<div class="d-flex p-2 m-5"></div>
<div class="container">

  <ng-container *ngIf="!mascota; else divMascota">
    <div class="alert alert-warning" role="alert">
      No hay datos para mostrar
    </div>
  </ng-container>

  <ng-template #divMascota>

    <div fxLayout="row" fxLayout.xs="column">
      <div fxFlex="50">
        <h1>
          <strong>Raza: {{mascota.raza}}</strong>
          <small> / de {{mascota.origen}}</small>
        </h1>
        <br />
        <img class="img" [src]="mascota.img" alt="" />
      </div>

      <div fxFlex="50">
        <h1>
          {{mascota.peso}} <small>/ Guardian: {{mascota.guardian}}</small>
        </h1>
        <button mat-button class="btn btn-success m-2" (click)="regresar()"><i class="bi bi-backspace"></i></button>
        <button name="button" class="btn btn-primary" (click)="eliminar()">Eliminar</button>
        <br />
        <p>{{ mascota.des }}</p>
        <p>{{ mascota.salud }}</p>
        <p>{{ mascota.ejercicio }}</p>
        <p>{{ mascota.nutricion }}</p>
        <br>


      </div>
    </div>
  </ng-template>
</div>
