<div class="d-flex p-2 m-5"></div>
<div class="container">
  <h2>Formulario Reactivo</h2>
  <div class="card bordery colmd-12">
    <div class="card-header bg-info"><strong>Datos Generalizados</strong></div>
    <div class="card-body">
      <form
      class="mt-3"
      (ngsubmit)="guardar()"
      [formGroup]="formularioGeneral">

      <!-- elementos del formulario-->
      <div class="form-group row">
        <div class="form-group col-md-6">
          <input
          type="text"
          class="form-control form-control-sm"
          placeholder="Nombre"
          formControlName="nombre"
          />
        <div class="invalid-feedback">
          <!-- aqui la validacion-->
        </div>
      </div>
      <!-- aqui otro input--><
      <div class="form-group col-md-6">
        <input
        type="text"
        class="form-control form-control-sm"
        placeholder="Apellido"
        formControlName="apellido"
        />
      <div class="invalid-feedback">
        <!-- aqui la validacion-->
      </div>
    </div>
    </div>
    <!-- fin form-->
    <button type="submit" class="btn btn-sm tbn-outline-info mt-sm-2">
      <i class="bi bi-database-add"></i>Guardar
    </button>
    </form>
    </div>
  </div>
</div>
