import { Component } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';
import { IMAGE_CLOUDINARY, VIDEO_YOTUBE } from '@modules/directivas/constants/directivas.consts';

@Component({
  selector: 'app-ng-if',
  templateUrl: './ng-if.component.html',
  styleUrls: ['./ng-if.component.scss']
})
export class NgIFComponent {

  isError:boolean = true;
  msg: string = 'Resultado';
  dato: String = 'video';
  image: string = IMAGE_CLOUDINARY;
  resultado:boolean = true;

  constructor(private sanitizer: DomSanitizer) {}
  get video(){
    return this.sanitizer.bypassSecurityTrustResourceUrl(VIDEO_YOTUBE);
  }

}
<p>ng-if works!</p>
<div class="container mt-5" >
    <div class="msg" *ngIf="resultado">
        <p><Strong> {{ msg }}</Strong></p>
    </div>
    <hr />
    <div class="contenido">
        <ng-container *ngIf="dato === 'image'; then Imagen; else Video">

        </ng-container>
        <ng-template #Imagen>
            <img [src]="image" width="50%" alt="..." />
        </ng-template>
        <ng-template #Video>
            <div class="ratio ratio-16x9 contenido">
                <iframe
                width="800"
                height="500"
                [src]="video"
                title="video"
                frameborder="0"
                allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                allowfullscreen
                ></iframe>
            </div>
        </ng-template>
    </div>
</div>
