import { Component } from '@angular/core';

@Component({
  selector: 'app-ng-style',
  templateUrl: './ng-style.component.html',
  styleUrls: ['./ng-style.component.scss']
})
export class NgSTYLEComponent {
  size: number = 14;
  bandera: boolean =true;

  colorEstado:string ='#f00';
  message =1;

  get color(){
    return '#00f';
  }
}
<!--<p>ng-style works!</p>-->
<div class = "container mt-5">
  <!--pasando un objeto-->
  <h1 [ngStyle]="{'color':colorEstado}">Trabajando con ngStyle</h1>
  <h2 [ngStyle]="{'color':color}">Con objeto y Método</h2>
  <h3 [ngStyle]="{'color':message ===1 ?'green':'red'}">Mensaje Adicional</h3>
  <hr>

  <br>
  <p [style.fontSize.px]="size">
    Con NgStyle cambiamos el Style:fontSize el texto.
  </p>
  <button class="btn btn-primary m-1" (click)="size=size + 5"> + </button>
  <button class="btn btn-primary m-1" (click)="size=size - 5"> - </button>

  <hr>

  <button class="btn" [class.btn-primary]="bandera" (click)="bandera=!bandera "> Enviar </button>

</div>
