import { Component } from '@angular/core';

@Component({
  selector: 'app-bin-ding',
  templateUrl: './bin-ding.component.html',
  styleUrls: ['./bin-ding.component.scss']
})
export class BinDINGComponent {

  habilitar: boolean = false;

  clase: string = 'btn btn-danger';

  txtPlaceHolder: string = 'Su nombre';

  txtType: string = 'radio';

  isChecked: boolean = true;

  cambiarPropiedad(){
    this.habilitar =! this.habilitar;
    this.txtPlaceHolder = 'Deshabilitado';
    this.txtType = 'checkbox';
    this.isChecked =! this.isChecked;
    this.clase = 'btn btn-primary';
  }


}
<div class="container mt-5">
<p>bin-ding works!</p>
<hr/>
<!-- utilizamos un binding para cmabiar la propiedad disable del input -->
<input type="text" class="form-control" [placeholder]="txtPlaceHolder" [disabled]="habilitar" >
<br>
<input [type]="txtType" class="form-check-input" [checked]="isChecked">
<hr/>
<button
  type="button"
  [class]="clase"
  [disabled]="habilitar"
  >
  Disabled Button
</button>

<div class="form-check form-switch mt-3">
  <input class="form-check-input" type="checkbox" role="switch" id="felxSwitchCheckDefault"
  (click)="cambiarPropiedad()">
  <label class="form-check-label" for="felxSwitchCheckDefault">Estado</label>
</div>
<hr />
</div>
